wp.customize.controlConstructor["kirki-sortable"]=wp.customize.Control.extend({ready:function(){var a=this;if(!_.isUndefined(window.kirkiControlLoader)&&_.isFunction(kirkiControlLoader)){kirkiControlLoader(a)}else{a.initKirkiControl()}},initKirkiControl:function(){var a=this;a.container.find(".kirki-controls-loading-spinner").hide();a.sortableContainer=a.container.find("ul.sortable").first();a.sortableContainer.sortable({stop:function(){a.updateValue()}}).disableSelection().find("li").each(function(){jQuery(this).find("i.visibility").click(function(){jQuery(this).toggleClass("dashicons-visibility-faint").parents("li:eq(0)").toggleClass("invisible")})}).click(function(){a.updateValue()})},updateValue:function(){var b=this,a=[];this.sortableContainer.find("li").each(function(){if(!jQuery(this).is(".invisible")){a.push(jQuery(this).data("value"))}});b.setting.set(a)}});