jQuery(document).ready(function(){function a(b){_.each(kirkiTooltips,function(c){let trigger,controlID,content;if(c.id!==b.id){return}if(b.container.find(".tooltip-content").length){return}trigger='<span class="tooltip-trigger" data-setting="'+c.id+'"><span class="dashicons dashicons-editor-help"></span></span>';controlID="#customize-control-"+c.id;content='<div class="tooltip-content hidden" data-setting="'+c.id+'">'+c.content+"</div>";jQuery('<div class="tooltip-wrapper">'+trigger+content+"</div>").prependTo(controlID);jQuery('.tooltip-trigger[data-setting="'+c.id+'"]').on("click",function(){jQuery('.tooltip-content[data-setting="'+c.id+'"]').toggleClass("hidden")})});jQuery(document).mouseup(function(c){if(!jQuery(".tooltip-content").is(c.target)){if(!jQuery(".tooltip-content").hasClass("hidden")){jQuery(".tooltip-content").addClass("hidden")}}})}wp.customize.control.each(function(b){wp.customize.section(b.section(),function(c){if(c.expanded()||wp.customize.settings.autofocus.control===b.id){a(b)}else{c.expanded.bind(function(d){if(d){a(b)}})}})})});